import React, { useState } from "react";
import ToDoItem from "./ToDoItem";
import InputArea from "./InputArea";

function App() {
	const [items, setItems] = useState([]);

	function deleteItem(id) {
		setItems((prevItems) => {
			return prevItems.filter((item, index) => {
