	}
	return (
		<div className='container'>
			<div className='heading'>
				<h1>To-Do List</h1>
			</div>
			<InputArea />
			<div>
				<ul>
					{items.map((todoItem, index) => (
						<ToDoItem
							key={index}
							id={index}
							text={todoItem}
							onChecked={deleteItem}
						/>
					))}
				</ul>
			</div>
		</div>
	);
}

export default App;
