import React, { useState } from "react";

function App() {
	let time = new Date().toLocaleTimeString();
	const [currentMsg, setTime] = useState(time);
	function updateTime() {
		setTime(time);
	}
	setInterval(updateTime, 1000);
	return (
		<div className='container'>
			<h1>{currentMsg}</h1>
			<button onClick={updateTime}>Get Time</button>
		</div>
	);
}

export default App;
