import React, { useState } from "react";

function App() {
	let time = new Date().toLocaleTimeString();
	const [currentTime, setTime] = useState("Time");
	function updateTime() {
		currentTime = time;
	}
	setInterval(updateTime, 1000);
	return (
		<div className='container'>
			<h1>{currentTime}</h1>
			<button onClick={updateTime}>Get Time</button>
		</div>
	);
}

export default App;
